create  table if not exists district
(
    id        serial
    constraint district_pkey
    primary key,
    region_id integer
    constraint "fk-district-region_id-region-id"
    references region
    on delete cascade,
    name      jsonb,
    status    integer default 1,
    top       integer default 0
);

create index "idx-district-region_id"
    on district (region_id);

